import React, { useState, useEffect } from "react";

import oglasiService from "../../services/oglasi.service";
import {Content, StyledTable, StyledButton, StyledCard} from "../../components/BasicStyledComponents";
import {Button, Card, Typography} from "antd";
import AdComponent from "./components/Ad";

const Index = () => {
    const [oglasi, setOglasi] = useState([]);
    /*
    useEffect(() => {
        oglasiService.getAll().then((res) => setOglasi(res.data));

    }, []);
     */
    const columns = [
        {
            title: "Sadržaj",
            dataIndex: "sadrzaj",
            key: 'sadrzaj'
        },
        {
            title: "Mjesto",
            dataIndex: "mjesto",
            key: "mjesto"
        },
        {
            title: "Napomena",
            dataIndex: "nampomena",
            key: "nampomena",
        },
        {
            title: "Datum",
            dataIndex: "datum",
            key: "datum"
        },
        {
            title: "Potrebno radnika",
            dataIndex: "brojLjudi",
            key: "brojLjudi"
        },
        {
            title: "Satnica",
            dataIndex: "satnica",
            key: "satnica"
        },
        {
            title: "Aktivan do",
            dataIndex: "aktivanDo",
            key: "aktivanDo"
        },
        {
            title: "Narucilac",
            dataIndex: "narucilac",
            key: "narucilac"
        },
        {
            title: "Posao",
            dataIndex: "posaoTip",
            key: "posaoTip"
        },
        {
            title: "Novcana naknada",
            dataIndex: "novcanaNaknada",
            key: "novcanaNaknada"
        },
        ];

    const test = [
        {
            key: '1',
            sadrzaj: "sadrzajoglasa",
            mjesto: 'BL',
            napomena: 'napomena',
            datum: '2.2.2022',
            brojLjudi: '4',
            aktivanDo: '3.3.333',
            satnica: "5KM",
            tip: "radi se",
            novcanaNaknada: '44',
            narucilac: 'neka firma'
        }
    ];

    const {tekst} = Typography;

    const styledCardsArray = [
        {
            naslov:"prva",
            sadrzaj:"sadrzaj prve",
            slika:"Slika"
        },
        {
            naslov: "druga",
            sadrzaj: "sadrzaj druge",
            slika:"Slika"
        },
    ];
    return (
        <div>
            {/* Your app content */}
            <h1>OGLASI</h1>
            <div className="cardContainter">
            {
                styledCardsArray.map((card, br) => (
                    <AdComponent>
                        key={br}
                        naslov={card.naslov}
                        sadrzaj={card.sadrzaj}
                    </AdComponent>
                ))
            }
            </div>

        </div>
    );

    /*
          <AdComponent naslov={tekst} sadrzaj="SADRZAJ"></AdComponent>
          <AdComponent naslov="NASLOV" sadrzaj="SADRZAJ"></AdComponent>
          <AdComponent naslov="NASLOV" sadrzaj="SADRZAJ"></AdComponent>
          */

  //  return (
     //   <Card title = "Naslov"> SADRZAJ </Card>
       /* <Content>
            <StyledTable
                key="id"
              //  dataSource={oglasi}
                dataSource={test}
                columns={columns}
            />
            <StyledButton>
                Obrisi
            </StyledButton>
        </Content>

        */
 //   );
    
};


export default Index;
