                    <div className="modal">
                                <Form.Item
                                    name="naziv"
                                    rules={[{ required: true, message: "fieldRequired" }]}
                                >
                                    <TextField id="naziv" label="Naziv" variant="standard" />
                                </Form.Item>
                    </div>
                </Form>
            </div>
        </div>
    );
};
export default UpravljanjeNalozima;
