import {StyledCard, StyledContent, StyledDataAndTime} from "../../../components/BasicStyledComponents";
import {DatePicker} from "antd";

const AdComponent = ({naslov,sadrzaj,mjesto,brojLjudi,datum,satnica,aktivanDo, slika}) => {

    const formattedDate = datum.toLocaleDateString('en-GB', {
        day: '2-digit',
        month: '2-digit',
        year: '2-digit'
    });

    return (
        <StyledCard title={naslov}>
            <StyledContent>{sadrzaj}</StyledContent>
            <p style={{fontWeight:"bold"}}> Lokacija: </p>
            <p>   {mjesto}</p>
            <p style={{fontWeight:"bold"}}> Potrebno radnika: </p>
            <p> {brojLjudi}</p>
            <p style={{fontWeight:"bold"}}> Cijena: {satnica} KM</p>
            <p> Datum: {formattedDate} </p>
            <p> Aktivan do: {aktivanDo}</p>
            <p>{slika}</p>
        </StyledCard>
    );
};


export default AdComponent;
