import base from "./base.service";

const instance = base.service(true);

export const getAll = () => {
    return instance.get("/oglasi");
};
export const insert = (ad) => {
    return instance.post("/oglasi", ad).then((res) => res.data);
};
export const remove = (id) => {
    return instance.delete(`/oglasi/${id}`);
};
export const update = (ad) => {
    return instance
        .put(`/oglasi/${ad.id}`, ad)
        .then((res) => res.data);
};
export const getMyAds = () => {
    return instance.get(`/oglasi`);
};
export const getAllOglasiById = (id) => {
    return instance.get(`korisnici/${id}/oglasi`);
};
export default {
    getAll,
    insert,
    remove,
    update,
    getMyAds,
    getAllOglasiById
};
